pageContext.rootQuery:
query ProjectQuery($uid: String) {
  prismic {
    allProjects(uid: $uid) {
      edges {
        node {
          _meta {
            id
            uid
            type
          }
          title
          body {
            __typename
            ... on PRISMIC_ProjectBodyText{
              type
              label
              primary{
                text
              }
            }
          }
        }
      }
    }
  }
}
